LECT sum(new_member_acquisition_gmv) FROM activity_media_slot_analysis WHERE campaign_id = #{cid} ),2) as nmagAcc , ROUND(a.next_month_active_member_count / (SELECT sum(next_month_active_member_count) FROM activity_media_slot_analysis WHERE campaign_id = #{cid} ),2) as nmamcAcc , ROUND(a.next_month_active_member_gmv / (SELECT sum(next_month_active_member_gmv) FROM activity_media_slot_analysis WHERE campaign_id = #{cid} ),2) as nmamgAcc , ROUND(a.next_month_new_member_count / (SELECT sum(next_month_new_member_count) FROM activity_media_slot_analysis WHERE campaign_id = #{cid} ),2) as nmnmcAcc , ROUND(a.next_month_new_member_gmv / (SELECT sum(next_month_new_member_gmv) FROM activity_media_slot_analysis WHERE campaign_id = #{cid} ),2) as nmnmgAcc , ROUND(a.cost/a.exposure_count*1000,2) as cpm , ROUND(a.monthly_active_member_gmv/a.cost,2) as roi , ROUND(a.cost/a.monthly_active_member_count,2) as cac , ROUND(a.monthly_active_member_count/b.periodic_monthly_active_user_count,2) as mamcRate , ROUND(a.monthly_active_member_gmv/b.periodic_monthly_active_user_gmv,2) as mamcGmvRate , ROUND(a.new_member_acquisition_gmv/a.cost,2) as rnmRoi , ROUND(a.cost/a.new_member_acquisition_count,2) as rnmCac , ROUND(a.new_member_acquisition_count/b.periodic_monthly_active_user_count,2) as nmcRate , ROUND(a.new_member_acquisition_gmv/b.periodic_monthly_active_user_gmv,2) as nmcGmvRate , ROUND(a.monthly_active_member_gmv/a.monthly_active_member_count,2) as pcmaGmv , ROUND(a.next_month_active_member_gmv/a.next_month_active_member_count,2) as mpcmaGmv , ROUND(a.new_member_acquisition_gmv/a.new_member_acquisition_count,2) as pcnGmv , ROUND(a.next_month_new_member_gmv/a.next_month_new_member_count,2) as panGmv , ROUND(a.next_month_active_member_count/a.monthly_active_member_count,2) as masmrRate , ROUND(a.next_month_new_member_count/a.new_member_acquisition_count,2) as mrrrRate , ROUND((select avg( cost/ exposure_count*1000) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as cpmAvg , ROUND((select avg( monthly_active_member_gmv/ cost) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as roiAvg , ROUND((select avg( cost/ monthly_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as cacAvg , ROUND((select avg( monthly_active_member_count/b.periodic_monthly_active_user_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as mamcRateAvg , ROUND((select avg( monthly_active_member_gmv/b.periodic_monthly_active_user_gmv) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as mamcGmvRateAvg , ROUND((select avg( new_member_acquisition_gmv/ cost) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as rnmRoiAvg , ROUND((select avg( cost/ new_member_acquisition_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as rnmCacAvg , ROUND((select avg( new_member_acquisition_count/b.periodic_monthly_active_user_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as nmcRateAvg , ROUND((select avg( new_member_acquisition_gmv/b.periodic_monthly_active_user_gmv) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as nmcGmvRateAvg , ROUND((select avg( monthly_active_member_gmv/ monthly_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as pcmaGmvAvg , ROUND((select avg( next_month_active_member_gmv/ next_month_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as mpcmaGmvAvg , ROUND((select avg( new_member_acquisition_gmv/ new_member_acquisition_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as pcnGmvAvg , ROUND((select avg( next_month_new_member_gmv/ next_month_new_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as panGmvAvg , ROUND((select avg( next_month_active_member_count/ monthly_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as masmrRateAvg , ROUND((select avg( next_month_new_member_count/ new_member_acquisition_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as mrrrRateAvg , ROUND(a.cost/a.exposure_count*1000,2) -ROUND((select avg( cost/ exposure_count*1000) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as cpmDiff , ROUND(a.monthly_active_member_gmv/a.cost,2)-ROUND((select avg( monthly_active_member_gmv/ cost) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as roiDiff , ROUND(a.cost/a.monthly_active_member_count,2) -ROUND((select avg( cost/ monthly_active_member_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as cacDiff , ROUND(a.monthly_active_member_count/b.periodic_monthly_active_user_count,2) -ROUND((select avg( monthly_active_member_count/b.periodic_monthly_active_user_count) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as mamcRateDiff , ROUND(a.monthly_active_member_gmv/b.periodic_monthly_active_user_gmv,2)-ROUND((select avg( monthly_active_member_gmv/b.periodic_monthly_active_user_gmv) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as mamcGmvRateDiff , ROUND(a.new_member_acquisition_gmv/a.cost,2)-ROUND((select avg( new_member_acquisition_gmv/ cost) from activity_media_slot_analysis where a.media=media and a.contact_point=contact_point ),2) as rnmRoiDiff , ROUND(a.cost/a.new_member_acquisition_count,2) -ROUND((select avg( cost/ new_me